String.prototype.isNullOrEmpty = function () { return null === this || 0 === this.length }, String.prototype.isNullOrWhitespace = function () { return null === this || 0 === this.replace(/\s/g, "").length }, String.prototype.cleanRepetations = function () { return this.trim().replace(/\s+/g, " ") }, String.prototype.words = function (t) { return this.trim(t).cleanRepetations(t).split(t || /\s+/) }, String.prototype.replaceAll = function (t, r) { return this.replace(new RegExp(t, "g"), r) }, String.prototype.trimLeft = function (t) { return t = t || " ", this.replace(new RegExp("^[" + t + "]+"), "") }, String.prototype.trimRight = function (t) { return t = t || " ", this.replace(new RegExp("[" + t + "]+$"), "") }, String.prototype.trim = function (t) { return this.trimLeft(t).trimRight(t) }, String.prototype.startsWith = function (t) { return this.substring(0, t.length) === t }, String.prototype.endsWith = function (t) { return this.substring(this.length - t.length) === t }, String.prototype.append = function (t) { return this + t }, String.prototype.prepend = function (t) { return t + this }, String.prototype.isEmail = function () { return /^(?:[a-z0-9!#$%&'*+\/=?^_`{|}~-]+(?:\.[a-z0-9!#$%&'*+\/=?^_`{|}~-]+)*@(?:[a-z0-9](?:[a-z0-9\-]*[a-z0-9])?\.)+[a-z0-9](?:[a-z0-9\-]*[a-z0-9])?)$/i.test(this) }, String.prototype.isURL = function () { return /^(?:\b(https?|ftp|file):\/\/[\-A-Z0-9+&@#\/%?=~_|!:,.;]*[\-A-Z0-9+&@#\/%=~_|])$/i.test(this) }, String.prototype.format = function () { for (a in x = this, arguments) x = x.replace("{" + a + "}", arguments[a]); return x };