String.prototype.isNullOrEmpty=function(){return this===null||this.length===0};String.prototype.isNullOrWhitespace=function(){return this===null||this.replace(/\s/g,"").length===0};String.prototype.cleanRepetations=function(){return this.trim().replace(/\s+/g," ")},String.prototype.words=function(e){return this.trim(e).cleanRepetations(e).split(e||/\s+/)},String.prototype.replaceAll=function(e,t){return this.replace(new RegExp(e,"g"),t)};String.prototype.trimLeft=function(e){e=e||" ";return this.replace(new RegExp("^["+e+"]+"),"")};String.prototype.trimRight=function(e){e=e||" ";return this.replace(new RegExp("["+e+"]+$"),"")};String.prototype.trim=function(e){return this.trimLeft(e).trimRight(e)};String.prototype.startsWith=function(e){return this.substring(0,e.length)===e};String.prototype.endsWith=function(e){return this.substring(this.length-e.length)===e};String.prototype.append=function(e){return this+e};String.prototype.prepend=function(e){return e+this};String.prototype.isEmail=function(){return/^(?:[a-z0-9!#$%&'*+\/=?^_`{|}~-]+(?:\.[a-z0-9!#$%&'*+\/=?^_`{|}~-]+)*@(?:[a-z0-9](?:[a-z0-9\-]*[a-z0-9])?\.)+[a-z0-9](?:[a-z0-9\-]*[a-z0-9])?)$/i.test(this)};String.prototype.isURL=function(){return/^(?:\b(https?|ftp|file):\/\/[\-A-Z0-9+&@#\/%?=~_|!:,.;]*[\-A-Z0-9+&@#\/%=~_|])$/i.test(this)}